(function(Scratch){
  const image='data:image/svg+xml;base64,'
  
  class Gobomusic{
      constructor(){
	        this.musicPlayer=null;
          this.musicCurrentime=0;
          this.musicLong=0;
      }
    getInfo(){
      return {
            id:"Gobomusic",
            name:'Gobomusic',
            menuIconURI:image,
            blockIconURI: image,
	          color1: '#FFC93E',
            color2: '#FFC93E',
            blocks:[
                {
                    opcode:"startmusic",
                    blockType:Scratch.BlockType.COMMAND,
                    text:"播放音乐[url]",
		                arguments: {
                      url:{
                        type:Scratch.ArgumentType.STRING,
                        defaultvalue:"link"
                      }
		                        },
                
		            },
                {
                    opcode:"pausemusic",
                    blockType:Scratch.BlockType.COMMAND,
                    text:"暂停音乐",
                
		            },
              {
                    opcode:"goonmusic",
                    blockType:Scratch.BlockType.COMMAND,
                    text:"继续播放音乐",
                
		            },
                  {
                    opcode:"stopmusic",
                    blockType:Scratch.BlockType.COMMAND,
                    text:"彻底停止音乐",
                
		            },
                {
                    opcode:"musicduration",
                    blockType:Scratch.BlockType.REPORTER,
                    text:"音乐时长",
                
		            },
                              {
                    opcode:"musicCurrenttime",
                    blockType:Scratch.BlockType.REPORTER,
                    text:"当前时间",
                
		            },
                              {
                    opcode:"turnto",
                    blockType:Scratch.BlockType.COMMAND,
                    text:"跳转到[time]",
		                arguments: {
                      time:{
                        type:Scratch.ArgumentType.STRING,
                        defaultvalue:"0"
                      }
		                        },
                
		            },
            ]
        
        }
    }
    startmusic(args,util){
      let link=args.url;
      if(this.musicPlayer){
        this.musicPlayer.pause()
        this.musicPlayer=null
      }
      this.musicPlayer=new Audio(link)
      this.musicLong=this.musicPlayer.duration
      this.musicPlayer.play()
    }
    pausemusic(){
      if(this.musicPlayer){
        this.musicPlayer.pause()
      }
    }
    goonmusic(){
      if(this.musicPlayer){
        this.musicPlayer.play()
      }
    }
    stopmusic(){
      if(this.musicPlayer){
        this.musicPlayer.pause()
        this.musicPlayer=null;
        this.musicLong=0;
        this.musicCurrenttime=0;
      }
      
    }
    musicduration(){
        return this.musicLong
    }
     musicCurrenttime(){
        if(this.musicPlayer){
            return this.musicPlayer.currentTime;
      }
        return 0;
    }
    turnto(args){
      this.musicPlayer.currentTime=args.time;
    }
  }
  Scratch.extensions.register(new Gobomusic());
})(Scratch)
